mapper.select:
select  from tb_user
where del_state = 10

    and user_id = #{userId,jdbcType=VARCHAR}


    and user_account = #{userAccount,jdbcType=VARCHAR}


    and user_name = #{userName,jdbcType=VARCHAR}


    and user_role = #{userRole,jdbcType=INTEGER,javaType=com.wlinsk.ansure.basic.enums.UserRoleEnum,typeHandler=com.wlinsk.ansure.basic.handler.UniversalEnumHandler}

order by create_time desc
